package LeagueDraft;

public class Player {

	private int age;
	private int position;
	private int goals;
	
	public Player(int age, int position, int goals) {

		this.age = age;
		this.position = position;
		this.goals = goals;
	}

	public int getGoals() {
		// TODO Auto-generated method stub
		return goals;
	}

	public int getAge() {
		// TODO Auto-generated method stub
		return age;
	}

}